select Corpora_Info.corpora_id, Corpora_Info.source_file_name, Corpora_Info.source_file_size, Corpora_Info.created_at, Corpora_Info.trained_at from Corpora_Info join (select Corpora_Info."corpora_id", max(Corpora_Info."created_at") as most_recent_record from Corpora_Info group by corpora_id) p ON (Corpora_Info.corpora_id = p.corpora_id and Corpora_Info.created_at = p.most_recent_record) where Corpora_Info.active = True;